import React from 'react'
import {ERR_OK} from '../../utils/config'
import http from '../../utils/http'
import "./index.css"

class Property extends React.PureComponent{
    constructor(props) {
		super(props)
		this.state = {
            account: false,
            totalValue: '',
            valueUnit: '',
            info: '',
            list: []
        }
    }
    componentDidMount () {
        this.getData()
    }
    getData () {
        http.get('capital/capitalaccount/balance').then(res => {
          if (res.code === ERR_OK) {
            !res.data.totalValue ? this.state.totalValue = '0.00' : this.state.totalValue = res.data.totalValue
            this.setState({
                valueUnit : res.data.valueUnit,
                list : res.data.list
            })
            // this.setCoindetail(res.data.list)
          }
        })
    }
	render(){
		return (
            <div className="property-container">
                <div className="property">
                    <p className="font-gray">总资产折合(CNY)</p>
                    <p className="totalProperty">{this.state.totalValue}</p>
                    {
                        this.state.list.map((item, index) => {
                            return <p key={index}>
                                <span className="coinName">{item.coinName}</span>
                                <span className="font-gray">{item.amount}≈{item.value}CNY</span>
                            </p>
                        })
                    }
                    <p className="check">查看资产</p>
                </div>
            </div>
		)
	}
}

export default Property
